        public static bool IsValidSaveFile(byte[] RawSaveFile)
        {
            if (RawSaveFile.Length == NEOTWEWYPC_SAVE_LENGTH || RawSaveFile.Length == NEOTWEWYSWITCH_SAVE_LENGTH)
            {
                return true;
            }

            return false;
        }

        public static byte[] ProcessFile(byte[] RawSaveFile, out bool FromPcVer)
        {
            if (RawSaveFile.Length == NEOTWEWYSWITCH_SAVE_LENGTH)
            {
                FromPcVer = false;
                return FromSwitchSaveFile(RawSaveFile);
            }
            else if (RawSaveFile.Length == NEOTWEWYPC_SAVE_LENGTH)
            {
                FromPcVer = true;
                return FromPCSaveFile(RawSaveFile);
            }

            throw new ArgumentException("I attempted to convert a save file that wasn't valid.");
        }

        private static byte[] FromSwitchSaveFile(byte[] RawSaveFile)
        {
            using (MemoryStream DataStream = new MemoryStream())
            {
                byte[] SaveSlots = new byte[NEOTWEWY_SLOT_LENGTH * 10];
                Array.Copy(RawSaveFile, NEOTWEWYSWITCH_INIT, SaveSlots, 0, NEOTWEWY_SLOT_LENGTH * 10);

                DataStream.Write(NEOTWEWYPC_GLOBAL_DEFAULT, 0, NEOTWEWYPC_GLOBAL_DEFAULT.Length);
                DataStream.Write(SaveSlots, 0, SaveSlots.Length);
                DataStream.Write(NEOTWEWYPC_UNKNOWN_END, 0, NEOTWEWYPC_UNKNOWN_END.Length);

                byte[] GeneratedDecryptedData = DataStream.ToArray();

                using (MemoryStream EncryptedStream = new MemoryStream())
                {
                    Rijndael SaveRijndael = new Rijndael(SaveFile.RijndaelKey, SaveFile.RijndaelIv);
                    byte[] EncryptedData = SaveRijndael.Encrypt(GeneratedDecryptedData);

                    EncryptedStream.Write(NEOTWEWYPC_ENCRYPTED_MAGIC, 0, 32);
                    EncryptedStream.Write(EncryptedData, 0, EncryptedData.Length);

                    return EncryptedStream.ToArray();
                }
            }
        }

        private static byte[] FromPCSaveFile(byte[] RawSaveFile)
        {
            using (MemoryStream DataStream = new MemoryStream())
            {
                byte[] EncryptedData = new byte[NEOTWEWYPC_SAVE_LENGTH - 32];
                byte[] SaveSlots = new byte[NEOTWEWY_SLOT_LENGTH * 10];
                Array.Copy(RawSaveFile, 32, EncryptedData, 0, NEOTWEWYPC_SAVE_LENGTH - 32);

                Rijndael SaveRijndael = new Rijndael(SaveFile.RijndaelKey, SaveFile.RijndaelIv);
                byte[] DecryptedData = SaveRijndael.Decrypt(EncryptedData);
                Array.Copy(DecryptedData, NEOTWEWYPC_INIT, SaveSlots, 0, NEOTWEWY_SLOT_LENGTH * 10);

                DataStream.Write(NEOTWEWYSWITCH_MAGIC, 0, NEOTWEWYSWITCH_MAGIC.Length);
                DataStream.Write(NEOTWEWYSWITCH_GLOBAL_DEFAULT, 0, NEOTWEWYSWITCH_GLOBAL_DEFAULT.Length);
                DataStream.Write(SaveSlots, 0, SaveSlots.Length);
                DataStream.Write(NEOTWEWYSWITCH_UNKNOWN_END, 0, NEOTWEWYSWITCH_UNKNOWN_END.Length);

                return DataStream.ToArray();
            }
        }
